head.script:
function getElementInfo() {
    var html = document.documentElement;
    var svg = document.getElementsByTagNameNS("http://www.w3.org/2000/svg", "svg")[0];

    console.log(html.namespaceURI);
    console.log(html.prefix);
    console.log(html.localName);
    console.log(html.tagName);

    console.log(svg.namespaceURI);
    console.log(svg.prefix);
    console.log(svg.localName);
    console.log(svg.tagName);

}
